import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
  faTrash,
  faSquareArrowUpRight,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function ViewQuestions() {
  const [data, setData] = useState([]);
  const { subject, testid } = useParams();
  

 

  //const { subject } = match.params;
  const deleteQuestion = (id, question) => {
    if (
      window.confirm(`Please Click Ok if you want to delete this question ${question}`)
    ) {
      fetch("http://localhost:5000/deleteQuestion", {
        method: "DELETE",
        crossDomain: true,
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          "Access-Control-Allow-Origin": "*",
        },
        body: JSON.stringify({
          questionid: id,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          alert(data.data);
          console.log(data);
        });
    } else {
    }
  };

  useEffect(() => {
    fetch(`http://localhost:5000/subjects/${subject}/tests/${testid}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "ok") {
          setData(data.data);
        } else {
          console.log(data);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }, [subject,testid]);

  return (
    <div>
      <div className="auth-wrapper" style={{ height: "auto" }}>
        <div className="auth-inner" style={{ width: "auto" }}>
          <h3>All Questions:</h3>
          <h3>{data.data}</h3>
          <table class="centered" style={{ width: 350 }}>
            {/* {data.map((i) => {
              return (
                <tbody>
                  <tr>
                    <td>{i.questionid}</td>
                    <td>{i._id}</td>
                    <td>
                      <FontAwesomeIcon
                        center
                        icon={faTrash}
                        onClick={() => deleteTest(i._id, i.questionid)}
                      />
                    </td>
                  </tr>
                </tbody>
              );
            })} */}
          </table>
        </div>
      </div>
    </div>
  );
}
