import React, { useState, useEffect } from "react";
import {faTrash} from  "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";

export default function ViewSubject() {
  const [data, setData] = useState([]);

  useEffect(() => {
          getAllSubject();
                },[]);

 const getAllSubject = () =>{
      fetch("http://localhost:5000/subjects", {
                     method: "GET",
                   })
                   .then((res) => res.json())
                   .then((data) => {
                    console.log(data, "Subjects");
                    setData(data.data);
                  
     });
    }



      const deleteSubject = (id,name) => {
      if(window.confirm(`Please Click Ok if you want to delete subject ${name}`)){
        fetch("http://localhost:5000/deleteSubject", {
        method: "DELETE",
                     crossDomain: true,
                     headers: {
                       "Content-Type": "application/json",
                       Accept: "application/json",
                       "Access-Control-Allow-Origin": "*",
                     },
                     body: JSON.stringify({
                       subjectid: id,
                       
                     }),
                   })
                     .then((res) => res.json())
                     .then((data) => {
                      alert(data.data);
                      getAllSubject();
                     });
      }else{}        

  return (
    <div> 
      <h2>All Subjects:</h2>
      <div className="auth-wrapper">     
        <div className="auth-inner">
          <ul>
            {data.map((i) => (
              <li key={i._id}>
                <Link to={`/subjects/${i.name}/tests`}>{i.name}
                 <FontAwesomeIcon
                     center
                     icon={faTrash}
                     onClick={() => deleteSubject(i._id, i.name)}
                   />
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

}
