import React, { Component } from 'react'

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state={
            email: "",
            password: "",
        };
        this.handleSubmit = this.handleSubmit.bind(this); //binding function
    }

handleSubmit(e){ /*submit function*/
    e.preventDefault();
    const { email, password } = this.state;
    console.log( email, password);
    
    fetch("http://localhost:5000/login-user", { /* sending login-user API*/
        method: "POST",
        crossDomain: true,
        headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            "Access-Control-Allow-Origin": "*",
        },
        body: JSON.stringify({ /*passing email and password*/
            email,
            password,
        }),
    })
        .then((res) => res.json())
        .then((data) => {
            console.log(data, "userRegister");
            if(data.status==="ok"){
                alert("Login Successfully");
            window.localStorage.setItem("token", data.data ); /*storing token in nodejs(localStorage)*/
            window.localStorage.setItem("loggedIn", true);// to allow user logged in when open another window

            window.location.href="./dashboard";
            }
            if (data.error === "User not exists") {
                alert("User Not Exist");
            }
            if (data.status === "error") {
                alert("Wrong Password, please try again..");
            }
        });
}

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <h3>Sign In</h3>

                <div className="mb-3">
                    <label>Email address</label>
                    <input
                        type="email"
                        className="form-control"
                        placeholder="Enter email"
                        onChange={(e) => this.setState({ email: e.target.value })}
                    />
                </div>

                <div className="mb-3">
                    <label>Password</label>
                    <input
                        type="password"
                        className="form-control"
                        placeholder="Enter password"
                        onChange={(e) => this.setState({ password: e.target.value })}
                    />
                </div>

                <div className="mb-3">
                    <div className="custom-control custom-checkbox">
                        <input
                            type="checkbox"
                            className="custom-control-input"
                            id="customCheck1"
                        />
                        <label className="custom-control-label" htmlFor="customCheck1">
                            Remember me
                        </label>
                    </div>
                </div>

                <div className="d-grid">
                    <button type="submit" className="btn btn-primary">
                        Submit
                    </button>
                </div>
                <p className="forgot-password text-right">
                    Forgot <a href="/forgot-password">password?</a>
                </p>
            </form>
        )
    }
}
