import React, { Component, useState } from 'react';
import app from "./firebase_config";
import { getAuth, RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import { async } from '@firebase/util';


const auth = getAuth(app);
export default function SignUp() {
            const [fname ,setFname] =  useState("");
            const [lname ,setLname] =  useState("");
            const [email ,setEmail] =  useState("");
            const [phoneNumber ,setPhoneNumber] =  useState("");
            const [password ,setPassword] =  useState("");
            const [verifyButton ,setVerifyButton] =  useState(false);
            const [verifyOTP , setVerifyOTP] =  useState(false);
            const [otp ,setOTP] =  useState("");
            const [verified ,setVerified] =  useState("false");
            // const [userType ,setUserType] =  useState("");
            // const [secretKey ,setSecretkey] =  useState("");  
          
           
            
    const onCaptchaVerify = () => {
       
        window.recaptchaVerifier = new RecaptchaVerifier(
            'recaptcha-container' ,
        {
        'size': 'invisible',
         'callback': (response) => {
            // onSignInSubmit();
         // reCAPTCHA solved, allow signInWithPhoneNumber.
        // ...
         },
        
        }, auth
        );
    }
   

    const onSignInSubmit = () => {
        
        const phoneNumber = "+60" + phoneNumber;
        const appVerifier = window.recaptchaVerifier;
        signInWithPhoneNumber(auth, phoneNumber, appVerifier)
            .then((confirmationResult) => {
                // SMS sent. Prompt user to type the code from the message, then sign the
                // user in with confirmationResult.confirm(code).
                window.confirmationResult = confirmationResult;
                alert("OTP sended");
                this.setState({ verifyOTP: true });
                // ...
            }).catch((error) => {
                // Error; SMS not sent
                // ...
            });
    }

    const verifyCode = () => {
        window.confirmationResult.confirm(otp).then((result) => {
            // User signed in successfully.
            const user = result.user;
            console.log(user);
            alert("Verification Done");
            // this.setState({
            //     verified: true,
            //     verifyOTP: false,
            // })
            // ...
        }).catch((error) => {
            alert("Invalid OTP");
            // User couldn't sign in (bad verification code?)
            // ...
        });
    }

    const changeMobile = (e) => {
      setPhoneNumber(e.target.value);
      if(phoneNumber.length == 9){
        setVerifyButton(true);
      }
        // this.setState(setphoneNumber,()=>{
        //      if(phoneNumber.length===10){
        //         this.setState({
        //             verifyButton: true,
        //         });
        //     }
        // });
        
    }

    // { phoneNumber: e.target.value },function(){
    //         if(phoneNumber.length===10){
    //             this.setState({
    //                 verifyButton: true,
    //             });
    //         }

    //     }

    const handleSubmit = (e) => {
        // if(userType == "Admin" && secretKey != "secret"){
        //     e.preventDefault();
        //     alert("Invalid Admin");
        // } else{}
            e.preventDefault();
            
            
            if (verified){ 
        
            
            console.log(fname, lname, email, password, phoneNumber);
            fetch("http://localhost:5000/register", {
                method: "POST",
                crossDomain: true,
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json",
                    "Access-Control-Allow-Origin": "*",
                },
                body: JSON.stringify({
                    fname,
                    lname,
                    email,
                    password,
                    phoneNumber,
                    
                }),
            })
                .then((res) => res.json())
                .then((data) => {
                  if(data.error != "User Exists"){
                    console.log(data, "userRegister");
                    alert("User Created Successfully");
                    }else{
                    alert(data.error);
                  }

                });
              }else{
            alert("Please Verify Phone Number");
} 
};
         return (
          <form onSubmit={handleSubmit}>
            <h3>Sign Up</h3>
            //<div id="recaptcha-container"></div>

            {/* <div>
              Register As
              <input
                type="radio"
                name="UserType"
                value="User"
                onChange={(e) => setUserType(e.target.value)}
              />{" "}
              User
              <input
                type="radio"
                name="UserType"
                value="Admin"
               onChange={(e) => setUserType(e.target.value)}
              />{" "}
              Admin
            </div>

            {userType == "Admin" ? (
              <div className="mb-3">
                <label>Secret Key</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Secret Key"
                  onChange={(e) => setSecretKey(e.target.value)}
                />
              </div>
            ) : null} */}

            <div className="mb-3">
              <label>First name</label>
              <input
                type="text"
                className="form-control"
                placeholder="First name"
                onChange={(e) =>  setFname(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Last name</label>
              <input
                type="text"
                className="form-control"
                placeholder="Last name"
                onChange={(e) =>  setLname(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Email address</label>
              <input
                type="email"
                className="form-control"
                placeholder="Enter email"
                onChange={(e) =>  setEmail(e.target.value)}
              />
            </div>

            <div className="mb-3">
              <label>Phone Number(01x-xxxxxxx)</label>
              <input
                type="phoneNumber"
                className="form-control"
                placeholder="Enter Phone Number"
                onChange={(e) => changeMobile(e)}
              />
              {verifyButton ? (
                <input
                  type="button"
                  value={verified ==true? "Verified" : "Verify"}
                  onClick={onCaptchaVerify()}
                  style={{
                    backgroundColor: "#0163d2",
                    width: "100%",
                    padding: 8,
                    color: "white",
                    border: "none",
                  }}
                />
              ) : null}
            </div> 

             {verifyOTP ? (
              <div className="mb-3">
                <label>OTP</label>
                <input
                  type="number"
                  className="form-control"
                  placeholder="OTP"
                  onChange={(e) => setOTP(e.target.value)}
                />
                <input
                  type="button"
                  value="OTP"
                  onClick={
                    console.log('verifyyyyyy')
                    // verifyCode()
                  }
                  style={{
                    backgroundColor: "#0163d2",
                    width: "100%",
                    padding: 8,
                    color: "white",
                    border: "none",
                  }}
                />
              </div>
            ) : null}

            <div className="mb-3">
              <label>Password</label>
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                onChange={(e) =>  setPassword(e.target.value)}
              />
            </div>

            <div className="d-grid">
              <button type="submit" className="btn btn-primary">
                Sign Up
              </button>
            </div>
            <p className="forgot-password text-right">
              Already registered <a href="/sign-in">sign in?</a>
            </p>
          </form>
        );     
  }
